import useState from 'react'

const useCart = () => {
	const [cart, setCart] = useState({});

	const addToCart = (id, qty) => {
		if(cart[id])
			setCart(val => {
				return {...val, id: val[id].qty + qty}
			})
		else
			setCart(val => {
				return {...val, id: qty}
			})
	}

	return {cart, addToCart};
}
